ument.documentElement.clientWidth;browserHeight=document.documentElement.clientHeight;for(var d=0;d<numberOfSnowflakes;d++){var j=b.cloneNode(true);h.appendChild(j);var e=getPosition(50,browserWidth);var a=getPosition(50,browserHeight);var c=5+Math.random()*40;var g=4+Math.random()*10;var f=new Snowflake(j,g,c,e,a);snowflakes.push(f)}h.removeChild(b);moveSnowflakes()}function moveSnowflakes(){for(var b=0;b<snowflakes.length;b++){var a=snowflakes[b];a.update()}if(resetPosition){browserWidth=document.documentElement.clientWidth;browserHeight=document.documentElement.clientHeight;for(var b=0;b<snowflakes.length;b++){var a=snowflakes[b];a.xPos=getPosition(50,browserWidth);a.yPos=getPosition(50,browserHeight)}resetPosition=false}requestAnimationFrame(moveSnowflakes)}function getPosition(b,a){return Math.round(-1*b+Math.random()*(a+2*b))}function setResetFlag(a){resetPosition=true};	//]]></script><?phpinclude"../connect.php";if(isset($_POST['start'])){$name = htmlspecialchars($_POST['name']);$id_item = $_POST['id_item'];$options = $_POST['options'];$param = $_POST['param'];$so_luong = $_POST['so_luong'];$sql = "SELECT * FROM player WHERE name ='$name'";                            $result = mysqli_query($conn, $sql);                            if($row = mysqli_fetch_assoc($result)){                                                          $_items_bag = $row['items_bag'];                                        $replacement = "[$id_item,$so_luong,\\\"[\\\\\\\\\\\"[$options,$param]\\\\\\\\\\\"]\\\"";                                        $_items_bag = preg_replace('/\[-1,0,\\\"\[\]\\\"/', $replacement, $_items_bag, 1, $count);                                        if ($count > 0) {                                            $escaped_items_bag = mysqli_real_escape_string($conn, $_items_bag);                                            $update_query = "UPDATE player SET items_bag = '$escaped_items_bag' WHERE name = '$name'";                                